export function _capitalizeText(e,r="sentence"){switch(r){case"sentence":return e.charAt(0).toUpperCase()+e.slice(1).toLowerCase();case"title":return e.toLowerCase().split(" ").map((e=>e.charAt(0).toUpperCase()+e.slice(1))).join(" ");case"upper":return e.toUpperCase();case"lower":return e.toLowerCase();default:return e}}export function _tagStatus(e,r){r=r||e;let t=$("<p></p>").text(r);switch(t.css({"border-radius":"6px","min-width":"101px","max-width":"160px",display:"flex","justify-content":"center","align-items":"center","text-transform":"uppercase","font-weight":"500"}),e){case"success":t.css({background:"#D6FDC3",color:"#4EB71E",border:"1px solid #9ADE7A"});break;case"danger":t.css({background:"#FFCACA",color:"#B71E1E",border:"1px solid #F99292"});break;case"warning":t.css({background:"#feffd5",color:"#d1d401",border:"1px solid #c9cb00"});break;case"info":t.css({background:"#C3D2E8",color:"var(--color-nro-02)",border:"1px solid var(--color-nro-02)"});break;default:t.css({background:"#dedede",color:"#9d9d9d",border:"1px solid #9d9d9d"})}return t}export function _formatValue(e){return new Intl.NumberFormat("pt-BR",{style:"currency",currency:"BRL"}).format(e)}