export function _capitalizeText(e,t="sentence"){switch(t){case"sentence":return e.charAt(0).toUpperCase()+e.slice(1).toLowerCase();case"title":return e.toLowerCase().split(" ").map((e=>e.charAt(0).toUpperCase()+e.slice(1))).join(" ");case"upper":return e.toUpperCase();case"lower":return e.toLowerCase();default:return e}}